#import "Troll.h"

@implementation Troll

-(id)init
{
	if ((self = [super init]))
	{
	}
	return self;
}

-(void)dealloc
{
	[super dealloc];
}

-(BOOL)handleSpeech:(NSString*)text tokens:(NSArray*)tokens tokenSet:(NSSet*)tokenset context:(id<SEContext>)ctx
{
    if ([tokens count] >= 2 && [[tokens objectAtIndex:0] isEqualToString:@"sing"] &&
		([tokenset containsObject:@"troll"]))
	{
        
    [ctx sendAddViewsUtteranceView:@"Now Singing: Trolololo"];
    //album artwork
    NSDictionary* snipProps = [NSDictionary dictionaryWithObjectsAndKeys:@"Troll Face", @"text", @"http://s-ak.buzzfed.com/static/enhanced/web05/2011/3/18/1/enhanced-buzz-15854-1300424464-1.jpg", @"link", nil];
    [ctx sendAddViewsSnippet:@"singingSiriSnippet" properties:snipProps];
    //sing
    [ctx sendAddViewsUtteranceView:@""];
    
    [ctx sendRequestCompleted];
    
    return YES; // handled by extension
}
    
    return NO;
}

@end

